import React, { Component } from "react";
import "./Nav.css";

class Nav extends Component {

  openNav = () => {
    this.refs.mySidenav.style.width = "100%";
  }

  closeNav = () => {
    this.refs.mySidenav.style.width = "0";
  }
  render() {
    return (
      <nav>
        <div ref="mySidenav" className="sidenav">
          <a href="/" className="closebtn" onclick={this.closeNav}>
            &times;
          </a>
          <a href="/">About</a>
          <a href="#">Services</a>
          <a href="#">Clients</a>
          <a href="#">Contact</a>
        </div>

        <span style="font-size:30px;cursor:pointer" onclick={this.openNav}>&#9776;</span>

      </nav>
    );
  }
}

export default Nav;
